package day18_while_dowhile_loops;

public class Print0To100 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//0 to 100 print 
		int count = 0;
		
		while(count<101) {
			System.out.println(count);
			++count;
			
		}
		System.out.println("Count :"+ count);
		
	}

}
